export declare const style = "\n/* <component>base-clearable-control-box */\n:host([clearable]:hover) #layout:not(.empty) #suffix {\nopacity: 0;\npointer-events: none;\n}\n:host([clearable]:hover) #layout:not(.empty) #clear {\nopacity: var(--icon-opacity);\npointer-events: auto;\n}\n\n.with-clear {\npadding-right: var(--padding);\n}\n#clear {\nbox-sizing: border-box;\nflex: 0 0 auto;\nposition: relative;\ndisplay: block;\nwidth: var(--icon-size);\nheight: var(--icon-size);\nborder: 1px solid var(--fg);\nborder-radius: 50%;\nbackground-color: transparent;\nopacity: 0;\npointer-events: none;\ntransform: rotate(45deg);\ntransition: all var(--duration);\n}\n#clear::before,\n#clear::after {\ndisplay: block;\ncontent: '';\nposition: absolute;\ntop: 0;\nright: 0;\nbottom: 0;\nleft: 0;\nwidth: 2px;\nheight: 2px;\nbackground: var(--fg);\nmargin: auto;\n}\n#clear::before {\nwidth: 8px;\n}\n#clear::after {\nheight: 8px;\n}\n:host([clearable]:hover) #layout:not(.empty) #clear:hover {\nopacity: var(--icon-opacity-hover);\nborder-color: var(--fg);\n}\n#clear:hover::before,\n#clear:hover::after {\nbackground-color: var(--fg);\n}\n:host([clearable]:hover) #layout:not(.empty) #clear:active {\nopacity: var(--icon-opacity-active);\n}\n#clear:focus {\noutline: 0 none;\n}\n.with-suffix #clear {\nposition: absolute;\ntop: 0;\nright: var(--padding);\nbottom: 0;\nleft: auto;\nmargin: auto;\n}\n";
