export declare const style = "\n/* <component>input */\n:host(:focus:not([disabled])),\n:host(:focus-within:not([disabled])),\n:host(:hover:not([disabled])) {\n  --fg: var(--fg-base);\n  --bg: var(--bg-base);\n}\n\n:host {\n  height: var(--height);\n}\n\n#content {\n  flex: 1 1 100%;\n  width: 100%;\n  height: 100%;\n  font-size: inherit;\n  padding: 0 var(--padding);\n  border: 0 none;\n  line-height: 1;\n  background: transparent;\n  color: inherit;\n}\n.with-prefix #content,\n.with-loading #content {\n  padding-left: calc(var(--padding) - 2px);\n}\n.with-suffix #content,\n.with-clear #content {\n  padding-right: calc(var(--padding) - 2px);\n}\n\n#content:focus {\n  outline: 0 none;\n}\n";
